#include <stdio.h>

int main()
{
    int a, b;
    scanf("%d%d", &a, &b);
    printf("%d\n", a + b);
    return 0;
}

#include <iostream>

int main()
{
    int a, b;
    std::cin >> a >> b;
    std::cout << a + b << std::endl;
    return 0;
}

import java.io.*;
import java.util.*;

public class Main
{
    public static void main(String args[])
    {
        int a, b;
        Scanner cin = new Scanner(System.in);

        a = cin.nextInt();
        b = cin.nextInt();
        System.out.println(a + b);
    }
}
