#include <stdio.h>

int main()
{
    int a, b;
    int i, j, k, len = 5000;

    scanf("%d%d", &a, &b);
    printf("%d\n", a + b);

    for (i = 0; i < len; ++i)
        for (j = 0; j < len; ++j)
            for (k = 0; k < len; ++k)
                a = i + j + k;
    return 0;
}

