import java.io.*;
import java.util.*;

public class Main
{
    public static void main(String args[])
    {
        int a, b;
        int i, j, k, len = 50000;
        Scanner cin = new Scanner(System.in);

        a = cin.nextInt();
        b = cin.nextInt();
        System.out.println(a + b);

        for (i = 0; i < len; ++i)
            for (j = 0; j < len; ++j)
                for (k = 0; k < len; ++k)
                    a = (i + k) % (j + 1);
    }
}

