import java.io.*;
import java.util.*;

public class Main
{
    public static void main(String args[])
    {
        int a, b;
        int i;
        Scanner cin = new Scanner(System.in);

        a = cin.nextInt();
        b = cin.nextInt();
        System.out.println(a + b);

        char[] out = new char[1024 * 1024];
        for (i = 0; i < 1024 * 1024; ++i)
            out[i] = 'a';
        System.out.print(out);
    }
}

