#include <stdio.h>
#include <stdlib.h>

int s[50000];

int cmp(const void * a, const void * b)
{
    return *(int *)a > *(int *)b;
}

int main()
{
    int i, n;

    scanf("%d", &n);
    for (i = 0; i < n; ++i)
        scanf("%d", s + i);

    qsort(s, n, sizeof(int), cmp);
    for (i = 0; i < n; ++i)
        printf("%d ", s[i]);

    return 0;
}

#include <iostream>
#include <algorithm>

using namespace std;

int s[50000];

int main()
{
    int i, n;

    cin >> n;
    for (i = 0; i < n; ++i)
        cin >> s[i];

    sort(s, s + n);
    for (i = 0; i < n; ++i)
        cout << s[i] << " ";

    return 0;
}

import java.io.*;
import java.util.*;

public class Main
{
    public static void main(String[] args)
    {
        int a, b, n;
        int[] s = null;

        Scanner cin = new Scanner(System.in);
        n = cin.nextInt();

        s = new int[n];
        for (int i = 0; i < n; ++i)
            s[i] = cin.nextInt();

        Arrays.sort(s);

        for (int i = 0; i < n; ++i)
            System.out.print(s[i] + " ");
    }
}
